import { NavLink, useHistory } from "react-router-dom";
import { useState } from "react";
import style from "./StudentSignup.module.css";

function StudentSignup() {
  const [userData, setUserData] = useState({
    user_name: "",
    user_email: "",
    user_password: "",
  });

  const [confirmPassword, setConfirmPassword] = useState("");

  const history = useHistory();

  function onTextFieldChange(e) {
    setUserData({
      ...userData,
      [e.target.name]: e.target.value,
    });
  }

  function handlePasswordChange(e) {
    setConfirmPassword(e.target.value);
  }

  function handleSignup() {
    if (userData.user_password === confirmPassword) {
      // Simulate registration without backend call
      alert("Your account has been created");
      alert("Please Login");
      history.push("/StudentLogin");
    } else {
      alert("Passwords do not match");
    }
  }

  return (
    <div id={style.container}>
      <div id={style.formHeading}>
        <h1>Student Signup</h1>
        <p>Please complete the form below to register with us</p>
      </div>

      <div id={style.nameBox}>
        <label htmlFor="name">
          Name
          <input
            onChange={(e) => onTextFieldChange(e)}
            type="text"
            name="user_name"
            required
          />
        </label>
      </div>

      <div id={style.emailBox}>
        <label htmlFor="email">
          Email
          <input
            onChange={(e) => onTextFieldChange(e)}
            type="text"
            name="user_email"
            required
          />
        </label>
      </div>

      <div id={style.passwordBox}>
        <label htmlFor="password">
          Password
          <input
            onChange={(e) => onTextFieldChange(e)}
            type="password"
            name="user_password"
            required
          />
        </label>
      </div>

      <div id={style.confirmPasswordBox}>
        <label htmlFor="confirmPassword">
          Confirm Password
          <input
            onChange={(e) => handlePasswordChange(e)}
            type="password"
            name="confirmPassword"
            required
          />
        </label>
      </div>

      <button id={style.signup} onClick={handleSignup}>
        Sign Up
      </button>

      <div id={style.login}>
        Have an account? <NavLink exact to="/StudentLogin">Log in</NavLink>
      </div>

      {/* Add a link to the StudentLogin page */}
      <div id={style.login}>
        Already have an account? <NavLink exact to="/StudentLogin">Log in here</NavLink>
      </div>
    </div>
  );
}

export default StudentSignup;
