import React from 'react';
import { NavLink, useHistory } from 'react-router-dom';
import axios from 'axios';
import { useState } from 'react';
import style from './StudentSignup.module.css';
import backgroundImg from '../../images/5.jpg'; // Assuming you have an image named '5.jpg' in your images folder

function StudentSignup() {
  const [userData, setUserData] = useState({
    user_name: '',
    user_email: '',
    user_password: '',
  });

  function onTextFieldChange(e) {
    setUserData({
      ...userData,
      [e.target.name]: e.target.value,
    });
  }

  const [password, setPassword] = useState('');

  function handlePassword(e) {
    setPassword({ confirmPassword: e.target.value });
  }

  let history = useHistory();

  async function handleSignup() {
    if (userData.user_password === password.confirmPassword) {
      await axios.post('http://localhost:3333/user', userData);
      alert('Your account has been created');
      alert('Please log in');
      history.push('/StudentLogin');
    } else alert('Passwords do not match');
  }

  const backgroundStyle = {
    backgroundImage: `url(${backgroundImg})`,
    backgroundSize: 'cover',
    backgroundPosition: 'center',
    backgroundRepeat: 'no-repeat',
    height: '100vh',
    display: 'flex',
    flexDirection: 'column',
    alignItems: 'center',
    justifyContent: 'center',
    color: 'white',
  };

  return (
    <div style={backgroundStyle}>
      <div id={style.container}>
        <div id={style.formHeading}>
          <h1>Student Signup</h1>
          <p>Please complete the form below to register with us</p>
        </div>

        <div id={style.nameBox}>
          <label htmlFor="name">
            Name
            <input
              onChange={(e) => onTextFieldChange(e)}
              type="text"
              name="user_name"
              required
            />
          </label>
        </div>

        <div id={style.emailBox}>
          <label htmlFor="email">
            {' '}
            Email
            <input
              onChange={(e) => onTextFieldChange(e)}
              type="text"
              name="user_email"
              required
            />
          </label>
        </div>

        <div id={style.passwordBox}>
          <label htmlFor="password">
            {' '}
            Password
            <input
              onChange={(e) => onTextFieldChange(e)}
              type="password"
              name="user_password"
              required
            />
          </label>
        </div>

        <div id={style.confirmPasswordBox}>
          <label htmlFor="confirmPassword">
            Confirm Password
            <input
              onChange={(e) => handlePassword(e)}
              type="password"
              name="confirmPassword"
              required
            />
          </label>
        </div>

        <button id={style.signup} onClick={handleSignup}>
          Sign Up
        </button>

        <div id={style.login}>
          Have an account? <NavLink exact to="/StudentLogin">Log in</NavLink>
        </div>
      </div>
    </div>
  );
}

export default StudentSignup;
