import React, { useState } from "react";
import { NavLink, useHistory } from "react-router-dom";
import axios from "axios";
import style from "./AdminLogin.module.css";

function AdminLogin() {
  const [admin, setAdmin] = useState({
    admin_name: "",
    admin_password: "",
  });

  const [loginStatus, setLoginStatus] = useState("");

  function handleInput(e) {
    setAdmin({
      ...admin,
      [e.target.name]: e.target.value,
    });
  }

  let history = useHistory();

  async function login(e) {
    try {
      const value = await axios.get("http://localhost:3333/admin");

      if (
        value.data[0].admin_name === admin.admin_name &&
        value.data[0].admin_password === admin.admin_password
      ) {
        setLoginStatus("success");
        history.push("/AdminDashboard");
      } else {
        setLoginStatus("failure");
      }
    } catch (error) {
      console.error("Error fetching admin data", error);
      setLoginStatus("error");
    }
  }

  return (
    <div id={style.container}>
      <div id={style.containerHeadingBox}>
        <h1>Admin Login</h1>
      </div>

      <div id={style.emailBox}>
        <label htmlFor="email">
          Email
          <input
            name="admin_name"
            onChange={(e) => handleInput(e)}
            type="text"
            id={style.email}
          />
        </label>
      </div>

      <div id={style.passwordBox}>
        <label htmlFor="password">
          Password
          <input
            name="admin_password"
            onChange={(e) => handleInput(e)}
            type="password"
            id={style.password}
          />
        </label>
      </div>

      <button onClick={(e) => login(e)} id={style.login}>
        Login
      </button>

      {loginStatus === "failure" && (
        <p style={{ color: "red" }}>Wrong Admin name or password</p>
      )}

      {loginStatus === "error" && (
        <p style={{ color: "red" }}>An error occurred. Please try again.</p>
      )}

      <NavLink to="/" id={style.goBackLink}>
        Go Back
      </NavLink>
    </div>
  );
}

export default AdminLogin;
