import style from "./StudentLogin.module.css";
import { NavLink, useHistory } from "react-router-dom";
import { useState } from "react";

function StudentLogin() {
  const [user, setUser] = useState({
    user_email: "",
    user_password: "",
  });

  const history = useHistory();

  function onTextFieldChange(e) {
    setUser({
      ...user,
      [e.target.name]: e.target.value,
    });
  }

  async function handleLogin() {
    // Get signup details from localStorage
    const signupDetails = JSON.parse(localStorage.getItem("signupDetails")) || [];
    
    // Log signupDetails to the console
    console.log("Signup Details:", signupDetails);

    // Check if the entered credentials match any signed-up user
    const matchingUser = signupDetails.find(
      (signupUser) =>
        signupUser.user_email === user.user_email &&
        signupUser.user_password === user.user_password
    );

    if (matchingUser) {
      alert("Login successful");
      sessionStorage.setItem("user", user.user_email);
      history.push("/StudentDashboard");
    } else {
      alert("Wrong User Email or password");
    }
  }

  return (
    <div id={style.container}>
      <div id={style.containerHeadingBox}>
        <h1>Student Login</h1>
      </div>

      <div id={style.emailBox}>
        <label htmlFor="email">
          Email
          <input
            name="user_email"
            onChange={(e) => onTextFieldChange(e)}
            type="text"
            id={style.email}
          />
        </label>
      </div>

      <div id={style.passwordBox}>
        <label htmlFor="password">
          Password
          <input
            name="user_password"
            onChange={(e) => onTextFieldChange(e)}
            type="password"
            id={style.password}
          />
        </label>
      </div>

      <button id={style.login} onClick={handleLogin}>
        Login
      </button>

      <div id={style.signup}>
        New to Portal? <NavLink exact to="/StudentSignup">Register</NavLink>
        <NavLink id={style.goBackLink} exact to="/">
          Go Back
        </NavLink>
      </div>
    </div>
  );
}

export default StudentLogin;
